DatabaseView.ViewDDL:
CREATE VIEW PA_SO_USER AS SELECT DISTINCT A.NAME AS SO_USER, B.FIRST_NAME + ' ' + B.LAST_NAME + ' - (' + A.NAME + ')' AS SO_USER_DESC
				FROM SY_PRNCPLS A, SY_USER_INFO B
							WHERE A.ID = B.ID;

CREATE VIEW PA_LIABILITY_LANG AS
SELECT '01' AS LIABILITY_LANG, 'Standard' AS LIABILITY_LANG_DESC UNION ALL
SELECT '02' AS LIABILITY_LANG, 'Modified' AS LIABILITY_LANG_DESC;

CREATE VIEW PA_STANDARD_MODIFIED AS
SELECT 'Standard' AS STANDARD_MODIFIED, 'Standard' AS STANDARD_MODIFIED_DESC UNION ALL		
SELECT 'Modified' AS STANDARD_MODIFIED, 'Modified' AS STANDARD_MODIFIED_DESC;

Measure.formula:
{CASE 
			WHEN PRICING_CONDITION = 'ZRNT' AND [CURRENT_PRICE] >0  THEN [TARGET_PRICE] * [SALES] / [CURRENT_PRICE] - [SALES]
			WHEN [CURRENT_PRICE] >0 THEN [TARGET_PRICE] * [QTY] - [CURRENT_PRICE] * [QTY] ELSE 0 END}

{CASE 
			WHEN PRICING_CONDITION = 'ZRNT' AND [CURRENT_PRICE] >0  THEN [MODIFIED_PRICE_1] * [SALES] / [CURRENT_PRICE] - [SALES]
			WHEN ISNULL(MODIFIED_PRICE_1, 0) != 0 THEN CASE WHEN [CURRENT_PRICE] >0 THEN [MODIFIED_PRICE_1] * [QTY] - [CURRENT_PRICE] * [QTY] ELSE 0 END ELSE 0 END}

{CASE 
			WHEN PRICING_CONDITION = 'ZRNT' AND [CURRENT_PRICE] >0  THEN [MODIFIED_PRICE_2] * [SALES] / [CURRENT_PRICE] - [SALES]
			WHEN ISNULL(MODIFIED_PRICE_2, 0) != 0 THEN CASE WHEN [CURRENT_PRICE] >0 THEN [MODIFIED_PRICE_2] * [QTY] - [CURRENT_PRICE] * [QTY] ELSE 0 END ELSE 0 END}

{CASE 
			WHEN PRICING_CONDITION = 'ZRNT' AND [CURRENT_PRICE] >0  THEN [MODIFIED_PRICE_3] * [SALES] / [CURRENT_PRICE] - [SALES]
			WHEN ISNULL(MODIFIED_PRICE_3, 0) != 0 THEN CASE WHEN [CURRENT_PRICE] >0 THEN [MODIFIED_PRICE_3] * [QTY] - [CURRENT_PRICE] * [QTY] ELSE 0 END ELSE 0 END}

{CASE 
			WHEN PRICING_CONDITION = 'ZRNT' AND [CURRENT_PRICE] >0  THEN [MODIFIED_PRICE_4] * [SALES] / [CURRENT_PRICE] - [SALES]
			WHEN ISNULL(MODIFIED_PRICE_4, 0) != 0 THEN CASE WHEN [CURRENT_PRICE] >0 THEN [MODIFIED_PRICE_4] * [QTY] - [CURRENT_PRICE] * [QTY] ELSE 0 END ELSE 0 END}

{CASE 
			WHEN PRICING_CONDITION = 'ZRNT' AND [CURRENT_PRICE] >0  THEN [RECOMMENDED_PRICE] * [SALES] / [CURRENT_PRICE] - [SALES]
			WHEN ISNULL(RECOMMENDED_PRICE, 0) != 0 THEN CASE WHEN [CURRENT_PRICE] >0 THEN [RECOMMENDED_PRICE] * [QTY] - [CURRENT_PRICE] * [QTY] ELSE 0 END ELSE 0 END}
